#include <iostream>
#include <vector>
#include <string>
#include <cstring>
using namespace std;

class Solution { // TODO: review & reflect
  public:
    const int near[4][2] = { {-1,0}, {0,1}, {1,0}, {0,-1}};

    bool exist(vector<vector<char>>& board, string word) {
      int height = board.size(), width = board.front().size(); 
      int * flag = new int[height * width]; // runtimeError if int flag[height*width]; ??

      for( int i = 0; i < height; i++) {
        for( int j = 0; j < width; j++) { 
          // memset(flag, 0, height * width * sizeof(int)); do not need, cost 52/88ms
          if( _exist(board, flag, i, j, word.c_str()) ) return true;
        }
      }
      delete [] flag;
      return false;
    }

    bool _exist(vector<vector<char>> & board, int *flag, int x, int y, const char * str) {
      // if(*str == '\0') return true;
      if( x < 0 || x >= (int)board.size() || y < 0 || y >= (int)board.front().size() ) return false;
      if(board[x][y] != *str) return false;

      flag[ x*board.front().size() + y ] = 1;

      for(int i = 0; i < 4; i++) {
        if(*(str+1) == '\0') return true;
        int new_x = x + near[i][0], new_y = y + near[i][1];
        if( flag[new_x*board.front().size() + new_y] != 1 &&  _exist(board, flag, new_x, new_y, str+1) ) return true;
      }

      flag[ x*board.front().size() + y ] = 0;

      return false;
    }

    bool existWithNoExtraData(vector<vector<char>>& board, string word) {
        if (board.size() == 0) return false;
        for (int i=0; i<board.size(); ++i) {
            for (int j=0; j<board[i].size(); ++j) {
                if (search(board, word, i, j, 0)) return true;
            }
        }
        return false;
    }

    bool search(vector<vector<char>>& board, string word, int i, int j, int pos) {

        if (pos == word.size()) return true;
        if ((i<0) || (i >= board.size()) || (j <0) || (j >= board[i].size())) return false;
        char c = board[i][j];
        if (c == word[pos]) {
            board[i][j] = '#';
            if (search(board, word, i - 1, j, pos + 1)) return true;
            if (search(board, word, i+1, j, pos+1)) return true;
            if (search(board, word, i, j-1, pos+1)) return true;
            if (search(board, word, i, j+1, pos+1)) return true;
            board[i][j] = c;
        }
        return false;
    }
};

void runtimeError1() { // Attn: often confound with x or y in 2d matrix
  vector<vector<char>> b {
    {'b'}, {'a'}, {'b'}, {'b'}, {'a'} 
  };

  Solution s;
  cout<<s.exist(b, "baa")<<endl;
}

